import React, { Component } from 'react';
import Table from '/home/armenuhi/projects/renderforest/src/components/table';
import '../../base.css';
class TablePage extends Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = {
      data: [
        { index: 1, name: 'name' },
        { index: 2, name: 'name1' },
        { index: 3, name: 'name2' },
        { index: 4, name: 'name3' },
        { index: 5, name: 'name4' },
      ]
    };

  }
  openInputField = (id) => {
    this.setState(() => {
      return {
        openField: true,
        activeIndex: id,
      }
    })

  }

  render() {
    const data = [
      { index: 1, name: 'name' },
      { index: 2, name: 'name1' },
      { index: 3, name: 'name2' },
      { index: 4, name: 'name3' },
      { index: 5, name: 'name4' },
    ]
    return (
      <div className='page'>
        <div className='description'>Should I eat at McDonalds?</div>
        <div className='tables-contanier'>
          <Table
            headerText='Pros'
            data={data}
            className='tableProps table border'
            openInputField={(index) => this.openInputField(index)}
            onInpitChange={(name,value)=>this.onInpitChange(name,value)}
          />
          <Table
            headerText='Cons'
            data={data}
            className='tableCons table'
            openInputField={this.openInputField}
          />
        </div>
      </div>
    );
  }
}

export default TablePage;
