import React from 'react';
import '/home/armenuhi/projects/renderforest/src/base.css'
import TableRow from './row';

const Table = ({ headerText, data, className, openInputField, onInpitChange, activeIndex }) => {
    return (
        <div className={`${className}`}>
            <span className='table-header'>{headerText}</span>

            <div className='table'>
                {
                    data.map((item) => {
                        return (
                            <TableRow
                                data={item}
                                openInputField={(index) => openInputField(index)}
                                onInpitChange={(name, value) => { onInpitChange(name, value) }}
                                activeIndex={activeIndex}
                            />
                        )
                    })
                }
            </div>
        </div>
    );
}

export default Table;
