import React from 'react';
import '/home/armenuhi/projects/renderforest/src/base.css'

const TableRow = ({ isInput, data, openInputField, onInpitChange, activeIndex }) => {
    return (
        <div className='row'>
            <span className='index'>{data.index}</span>
            {
                isInput && activeIndex === data.index && (
                    <input type='text' name='name' value={data.name}
                        onChange={(name, value) => onInpitChange(name, value)} />
                )
            }
            {
                !isInput && (
                    <span onClick={() => openInputField(data.index)}>{data.name}</span>
                )
            }
        </div>
    );
}

export default TableRow;
