import React, { Component } from 'react';
import Table from '/home/armenuhi/projects/renderforest/src/components/table';
import '../../base.css';
class TablePage extends Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = {
      data: {
        pros: [
          { index: 1, name: 'name' },
          { index: 2, name: 'name1' },
          { index: 3, name: 'name2' },
          { index: 4, name: 'name3' },
          { index: 5, name: 'name4' },
        ],
      }
    };

  }
  openInputField = (id) => {
    console.log(id)
    this.setState(() => {
      return {
        openField: true,
        activeIndex: id,
      }
    })

  }

  onInpitChange = (name, value, type) => {
    const newData = [...this.state.data, { [name]: value }]
    console.log(newData)
    this.setState(() => {
      return {
        data: newData,
        // data:[
        //   ...this.state.data,
        //   {
        //     ...this.state.data[name],
        //     [name]:value,
        //   }
        // ]
      }
    }, () => {
      console.log(this.state.data[name])
    })
  }

  render() {
    const { openField, data, activeIndex } = this.state
    return (
      <div className='page'>
        <div className='description'>Should I eat at McDonalds?</div>
        <div className='tables-contanier'>
          <Table
            headerText='Pros'
            data={data.props}
            className='tableProps table border'
            openInputField={(index) => this.openInputField(index)}
            onInpitChange={(name, value, headerText) => this.onInpitChange(name, value, headerText)}
            openField={openField}
            activeIndex={activeIndex}
          />
          {/* <Table
            headerText='Cons'
            data={data}
            className='tableCons table'
            openInputField={this.openInputField}
          /> */}
        </div>
      </div>
    );
  }
}

export default TablePage;
